mapper.select:
select
    *
from
    t_video
where
    userId = #{userId}

        and area = #{area}

order by id desc
limit #{start}, #{limit}

select
    *
from
    t_user_following
where
    userId = #{userId}

        and groupId = #{groupId}

order by id desc
limit #{start}, #{limit}

select
    *
from
    t_video_collection
where
    userId = #{userId}

        and groupId = #{groupId}

order by id desc
limit #{start}, #{limit}
